import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
import { useState } from 'react';
import Login from './pages/Login';
import Chat from './pages/Chat';
import History from './pages/History';
import Analytics from './pages/Analytics';
import { ChatBubbleLeftIcon, ClockIcon, ChartBarIcon, ArrowRightOnRectangleIcon, MoonIcon, SunIcon } from '@heroicons/react/24/outline';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('token'));
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
    document.documentElement.classList.toggle('dark');
  };

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <Router>
      <div className={`min-h-screen ${isDarkMode ? 'dark' : ''}`}>
        {isAuthenticated && (
          <div className="flex">
            {/* Sidebar */}
            <div className={`sidebar bg-gradient-to-b from-blue-900 to-purple-900 dark:from-gray-900 dark:to-gray-800 text-white transition-all duration-300 ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full md:translate-x-0'} md:w-64 h-screen md:h-auto p-6 md:sticky md:top-0 z-50`}>
              <div className="flex items-center justify-between mb-8">
                <div className="flex items-center">
                  <ChatBubbleLeftIcon className="w-8 h-8 mr-2" />
                  <h1 className="text-xl font-bold">Creator Platform</h1>
                </div>
                <button className="md:hidden text-white" onClick={toggleSidebar}>
                  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>
              </div>
              <nav className="sidebar-nav space-y-3 md:space-y-4">
                <Link to="/chat" className="sidebar-link" onClick={() => setIsSidebarOpen(false)}>
                  <ChatBubbleLeftIcon className="sidebar-icon" />
                  New Chat
                </Link>
                <Link to="/history" className="sidebar-link" onClick={() => setIsSidebarOpen(false)}>
                  <ClockIcon className="sidebar-icon" />
                  Content History
                </Link>
                <Link to="/analytics" className="sidebar-link" onClick={() => setIsSidebarOpen(false)}>
                  <ChartBarIcon className="sidebar-icon" />
                  Analytics
                </Link>
                <button
                  onClick={() => {
                    localStorage.removeItem('token');
                    setIsAuthenticated(false);
                    setIsSidebarOpen(false);
                  }}
                  className="sidebar-link text-red-400 hover:text-red-300"
                >
                  <ArrowRightOnRectangleIcon className="sidebar-icon" />
                  Logout
                </button>
              </nav>
              <button
                onClick={toggleDarkMode}
                className="mt-6 flex items-center p-4 w-full text-left rounded-xl bg-gray-800 hover:bg-gray-700 text-white"
              >
                {isDarkMode ? (
                  <>
                    <SunIcon className="sidebar-icon" />
                    Light Mode
                  </>
                ) : (
                  <>
                    <MoonIcon className="sidebar-icon" />
                    Dark Mode
                  </>
                )}
              </button>
            </div>
            {/* Mobile Toggle Button */}
            <button
              className="md:hidden fixed top-4 left-4 z-50 text-white bg-blue-600 p-2 rounded-full"
              onClick={toggleSidebar}
            >
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16" />
              </svg>
            </button>
            {/* Main Content */}
            <div className="flex-1 p-6 md:p-8">
              <Routes>
                <Route path="/login" element={<Login setIsAuthenticated={setIsAuthenticated} />} />
                <Route
                  path="/chat"
                  element={isAuthenticated ? <Chat /> : <Navigate to="/login" />}
                />
                <Route
                  path="/history"
                  element={isAuthenticated ? <History /> : <Navigate to="/login" />}
                />
                <Route
                  path="/analytics"
                  element={isAuthenticated ? <Analytics /> : <Navigate to="/login" />}
                />
                <Route path="/" element={<Navigate to="/login" />} />
              </Routes>
            </div>
          </div>
        )}
        {!isAuthenticated && (
          <Routes>
            <Route path="/login" element={<Login setIsAuthenticated={setIsAuthenticated} />} />
            <Route path="*" element={<Navigate to="/login" />} />
          </Routes>
        )}
      </div>
    </Router>
  );
}

export default App;
