import { useState, useEffect } from 'react';
import axios from 'axios';

function History() {
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchHistory = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/content/history', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setHistory(res.data);
      } catch (err) {
        console.error('Failed to fetch history');
      }
      setLoading(false);
    };
    fetchHistory();
  }, []);

  return (
    <div className="max-w-3xl mx-auto">
      <h2 className="text-xl font-semibold mb-4 text-gray-900 dark:text-white">Content History</h2>
      {loading ? (
        <p className="text-gray-700 dark:text-gray-200">Loading...</p>
      ) : history.length === 0 ? (
        <p className="text-gray-700 dark:text-gray-200">No content history available.</p>
      ) : (
        history.map((item) => (
          <div key={item._id} className="chatgpt-card mb-4">
            <p className="text-gray-700 dark:text-gray-200"><strong>Topic:</strong> {item.topic}</p>
            <p className="text-gray-700 dark:text-gray-200"><strong>Niche:</strong> {item.niche}</p>
            <p className="text-gray-700 dark:text-gray-200"><strong>Content:</strong> {item.content}</p>
            <p className="text-sm text-gray-500 dark:text-gray-400">{new Date(item.createdAt).toLocaleString()}</p>
          </div>
        ))
      )}
    </div>
  );
}

export default History;
