import { useState } from 'react';
import axios from 'axios';

function Chat() {
  const [topic, setTopic] = useState('');
  const [niche, setNiche] = useState('fashion');
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const niches = ['fashion', 'fitness', 'finance', 'food', 'travel'];

  const handleSubmit = async () => {
    setLoading(true);
    setError('');
    try {
      const res = await axios.post('http://localhost:5000/api/content', {
        topic,
        niche,
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setResponse(res.data);
    } catch (err) {
      setError('Failed to generate content. Please try again.');
    }
    setLoading(false);
  };

  return (
    <div className="max-w-3xl mx-auto">
      <div className="chatgpt-card">
        <h2 className="text-xl font-semibold mb-4 text-gray-900 dark:text-white">Content Idea Assistant</h2>
        <div className="mb-4">
          <input
            type="text"
            placeholder="Enter a topic (e.g., Summer Trends)"
            value={topic}
            onChange={(e) => setTopic(e.target.value)}
            className="chatgpt-input"
          />
        </div>
        <div className="mb-6">
          <select
            value={niche}
            onChange={(e) => setNiche(e.target.value)}
            className="chatgpt-input"
          >
            {niches.map((n) => (
              <option key={n} value={n}>{n.charAt(0).toUpperCase() + n.slice(1)}</option>
            ))}
          </select>
        </div>
        <button
          onClick={handleSubmit}
          disabled={loading || !topic}
          className="chatgpt-button w-full"
        >
          {loading ? 'Generating...' : 'Generate Content'}
        </button>
        {error && <p className="text-red-500 mt-4 text-center">{error}</p>}
        {response && (
          <div className="mt-6 p-6 bg-gray-50 dark:bg-gray-700 rounded-lg">
            <h3 className="text-lg font-semibold mb-2 text-gray-900 dark:text-white">Generated Content</h3>
            <p className="text-gray-700 dark:text-gray-200"><strong>Reel Idea:</strong> {response.reelIdea}</p>
            <p className="text-gray-700 dark:text-gray-200"><strong>Hook:</strong> {response.hook}</p>
            <p className="text-gray-700 dark:text-gray-200"><strong>Caption:</strong> {response.caption}</p>
            <p className="text-gray-700 dark:text-gray-200"><strong>Hashtags:</strong> {response.hashtags?.join(', ')}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default Chat;
