import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
import { useState } from 'react';
import Login from './pages/Login';
import Chat from './pages/Chat';
import History from './pages/History';
import Analytics from './pages/Analytics';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('token'));
  const [isDarkMode, setIsDarkMode] = useState(false);

  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
    document.documentElement.classList.toggle('dark');
  };

  return (
    <Router>
      <div className={`min-h-screen ${isDarkMode ? 'dark bg-gray-900' : 'bg-gray-100'}`}>
        {isAuthenticated && (
          <div className="flex">
            {/* Sidebar */}
            <div className="w-64 bg-gray-50 dark:bg-gray-800 h-screen p-4 shadow-lg">
              <h1 className="text-xl font-bold mb-6 text-gray-900 dark:text-white">Creator Platform</h1>
              <nav className="space-y-2">
                <Link to="/chat" className="block p-3 rounded-lg hover:bg-blue-100 dark:hover:bg-gray-700 text-gray-900 dark:text-white">
                  New Chat
                </Link>
                <Link to="/history" className="block p-3 rounded-lg hover:bg-blue-100 dark:hover:bg-gray-700 text-gray-900 dark:text-white">
                  Content History
                </Link>
                <Link to="/analytics" className="block p-3 rounded-lg hover:bg-blue-100 dark:hover:bg-gray-700 text-gray-900 dark:text-white">
                  Analytics
                </Link>
                <button
                  onClick={() => {
                    localStorage.removeItem('token');
                    setIsAuthenticated(false);
                  }}
                  className="block p-3 w-full text-left rounded-lg hover:bg-red-100 dark:hover:bg-red-900 text-red-600 dark:text-red-400"
                >
                  Logout
                </button>
              </nav>
              <button
                onClick={toggleDarkMode}
                className="mt-4 p-2 w-full text-left text-gray-900 dark:text-white"
              >
                {isDarkMode ? 'Switch to Light Mode' : 'Switch to Dark Mode'}
              </button>
            </div>
            {/* Main Content */}
            <div className="flex-1 p-6">
              <Routes>
                <Route path="/login" element={<Login setIsAuthenticated={setIsAuthenticated} />} />
                <Route
                  path="/chat"
                  element={isAuthenticated ? <Chat /> : <Navigate to="/login" />}
                />
                <Route
                  path="/history"
                  element={isAuthenticated ? <History /> : <Navigate to="/login" />}
                />
                <Route
                  path="/analytics"
                  element={isAuthenticated ? <Analytics /> : <Navigate to="/login" />}
                />
                <Route path="/" element={<Navigate to="/login" />} />
              </Routes>
            </div>
          </div>
        )}
        {!isAuthenticated && (
          <Routes>
            <Route path="/login" element={<Login setIsAuthenticated={setIsAuthenticated} />} />
            <Route path="*" element={<Navigate to="/login" />} />
          </Routes>
        )}
      </div>
    </Router>
  );
}

export default App;
